import React, {Component} from 'react'
import { NavLink } from 'react-router-dom'

import './noviProizvod.css';

export default class noviProizvod extends Component {

    constructor(props) {
        super(props);

        this.unesiProizvod = this.unesiProizvod.bind(this);
        this.unesititle = this.unesititle.bind(this);
        this.unesiauthor = this.unesiauthor.bind(this);

        this.state = {
          title: undefined,
          author:  undefined
        }
    }

    unesititle(e) {
      console.log("ime")
      this.setState(
        {
          "title": e.target.value
        });
    }

    unesiauthor(e) {
      console.log("ime2")

      this.setState(
        {
          "author": e.target.value
      });
    }



    unesiProizvod = async() => {

      console.log("ime3")
      if (this.state.title === undefined || this.state.author === undefined) 
      {
        console.log("ime3")
        console.log("Popunite sva polja!")
      } 
      else 
      {


        await fetch('/admin/unos-novog-proizvoda', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            title: this.state.title,
            author: this.state.author
          })
        });
      }

      document.querySelectorAll('input').forEach(input => 
        (input.value = "")
      );

      this.setState({title: undefined, author: undefined});
    }


    render() {
        return (

            <form>
              <div id="noviProizvod">

                <label> title </label>
                <input type="text" id="titleNovogProizvoda" onChange={ this.unesititle } placeholder="title..." required></input>


                <label> author  </label>
                <input type="text" id="authorNovogProizvoda" onChange={ this.unesiauthor } placeholder="author..." required></input>

                <button type="submit" id = "unosProizvoda" onClick={this.unesiProizvod}>
                  DODAJ NOVI PROIZVOD
                </button>

                <p> </p>
                <NavLink
                    to="/"> POCETNA
                </NavLink>
              </div>
            </form>
        )
    }
}
